<template>
    <div id="Notification">
        <ejs-toast ref='toastRef' id='toast_default' :position='position' :created='created' :close='onclose' :beforeOpen='onbeforeOpen'></ejs-toast>
    </div>
</template>

<script>
import Vue from "vue";
import { ToastPlugin, ToastCloseArgs } from "@syncfusion/ej2-vue-notifications";
import { isNullOrUndefined } from '@syncfusion/ej2-base';
Vue.use(ToastPlugin);
let ShowBtn = undefined;
export default {
    name: "Notification",
    data: function () {
        return {
            position: {
                X: 'Right'
            },
            ShowBtn: false
        }
    },
    props: {
        messages: {
            type: Array,
            required: true
        },
        mtype: {
            type: String,
            required: true
        }
    },
    mounted: function() {
        console.log('here');
        this.toasts = [
            { type:'warning', title: 'Warning!', content: '', cssClass: 'e-toast-warning', icon: 'fas fa-exclamation-triangle', showCloseButton: true, timeOut: 0 },
            { type:'success', title: 'Success!', content: '', cssClass: 'e-toast-success', icon: 'fas fa-check-circle',  showCloseButton: true, timeOut: 0 },
            { type:'error',   title: 'Error!', content: '', cssClass: 'e-toast-danger', icon: 'fas fa-times',  showCloseButton: true, timeOut: 0 },
            { type:'info', title: 'Information!', content: '', cssClass: 'e-toast-info', icon: 'fas fa-info-circle',  showCloseButton: true, timeOut: 0 }];
        if(this.messages.length > 0) {
            for(var i = 0; i<this.messages.length; i++) {
                var item = this.toasts.filter(item => item.type == this.mtype)[0];
                item.content = this.messages[i];
                this.$refs.toastRef.show(item);
            }
        }
    },

    methods: {
        created: function(args){
        },
        onclose: function(e){
            if (this.$refs.toastRef.$el.childElementCount === 0 ) {
                this.ShowBtn = false;
            }
        },
        onbeforeOpen: function(e){
            this.ShowBtn = true;
        }
    }
}
</script>

